pragma solidity 0.8.17;

import "@openzeppelin/contracts/access/Ownable.sol";

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

/**
 * @title ERC721u
 * @author Jourdan (PLAYA3ULL)
 * @notice This contract is intended to be inherited and provides
 *         access to a central location for handling retrieving,
 *         updating, and verifying ERC721 token URIs which the base
 *         ERC721 standard does not provide.
 *         Additionally, this ERC721u contract supports emitting
 *         events when the token URI is updated, allowing listeners
 *         to handle this change.
 */
contract ERC721u is ERC721, Ownable {
    /// @notice The base URI for tokens and where their metadata
    ///         is stored.
    string public baseTokenURI;

    /**
     * @dev Emit an event whenever the base token URI is updated.
     *
     * @param uri   The new base token uri.
     */
    event BaseTokenURIUpdated(string uri);

    /**
     * @dev Revert with an error when trying to access a token ID
     *      that does not exist.
     */
    error TokenDoesNotExist();

    constructor(
        string memory _name,
        string memory _symbol
    ) ERC721(_name, _symbol) {}

    /**
     * @dev Internal view function to view and return the base token URI.
     *
     * @return The base token URI where the metadata is stored.
     */
    function _baseURI() internal view virtual override returns (string memory) {
        return baseTokenURI;
    }

    /**
     * @notice Returns the token URI where the metadata is stored for
     *         a given token ID.
     *
     * @param tokenId   The ID of the token to retrieve the URI for.
     *
     * @return The token metadata URI.
     */
    function tokenURI(
        uint256 tokenId
    ) public view virtual override returns (string memory) {
        _assertTokenExists(tokenId);

        return
            string(abi.encodePacked(baseTokenURI, Strings.toString(tokenId)));
    }

    /**
     * @notice Sets the base token metadata URI.
     *
     * @param _newBaseTokenURI  The new base token URI.
     */
    function setBaseTokenURI(
        string memory _newBaseTokenURI
    ) external onlyOwner {
        baseTokenURI = _newBaseTokenURI;

        emit BaseTokenURIUpdated(baseTokenURI);
    }

    /**
     * @dev Internal view function to revert if the given token ID does
     *      not exist.
     *
     * @param tokenId   The ID of the token to verify if exists.
     */
    function _assertTokenExists(uint256 tokenId) internal view {
        if (!_exists(tokenId)) {
            revert TokenDoesNotExist();
        }
    }
}

contracts/nft/bull/interfaces/I3ULLNFT.s.content:
pragma solidity 0.8.17;

/**
 * @title IPLAYA3ULLNFT
 * @author Jourdan (PLAYA3ULL)
 * @notice IPLAYA3ULLNFT contains all external functions and
 *         structs for the 3ULL NFT.
 */
interface IPLAYA3ULLNFT {

    /**
     * @dev An NFT contains two components: a string that
     *      represents that NFTs DNA, preventing any other
     *      token from using the same DNA, and an array of
     *      traits that the NFT is constructed from.
     */
    struct NFT {
        string dna;
        uint256[] traits;
    }

    /**
     * @dev A trait represents the different parts of the
     *      token. Each trait contains three components: 
     *      a name, whether it is required, and the maximum
     *      number of attributes that this trait may hold.
     */
    struct Trait {
        string name;
        bool required;
        uint256 maxCount;
    }

    /**
     * @dev An attribute belongs to a trait, and represents
     *      a different and unique variation of the parent
     *      trait. Each attribute contains three components:
     *      a name, a rarity value, and the number of this
     *      attribute that has sold.
     */
    struct Attribute {
        string name;
        uint256 rarity;
        uint256 numSold;
    }

    function getMaxSupply() external view returns (uint256);

    function getTotalSupply() external view returns (uint256);

    function getTraitLength() external view returns (uint256);
    
    function getTrait(uint256 traitId) external view returns (Trait memory);

    function getAttributeLength(uint256 traitId) external view returns (uint256);

    function getAttribute(uint256 traitId, uint256 attrId) external view returns (Attribute memory);

    function isDNAUnique(uint256[] memory traitIds) external view returns (bool isUnique, string memory dna);

    function mint(address to, uint256[] memory traitIds) external;
}

