contracts/nft/games/mars-colony/ColonyMa.content:
pragma solidity 0.8.17;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";

import "@openzeppelin/contracts/utils/introspection/ERC165Storage.sol";
import "@openzeppelin/contracts/interfaces/IERC2981.sol";

import "../../../oracle/price/interfaces/IPriceOracle.sol";

import {ERC721u} from "../../ERC721u.sol";
import {IColonyMarsNFT} from "./interfaces/IColonyMarsNFT.sol";

/**
 * @title ColonyMarsNFT
 * @author Jourdan (PLAYA3ULL)
 * @notice This contract contains all storage and pricing
 *         data for the Colony Mars NFT tokens.
 */
contract ColonyMarsNFT is IColonyMarsNFT, ERC721u, ERC165Storage, IERC2981 {
    /// @notice The total number of tokens that currently exist.
    uint public totalSupply;

    /// @notice The address where royalties will be sent.
    address public royaltyAddress;

    /// @notice The interface for the Price Oracle to retrieve
    ///         pricing data from.
    IPriceOracle public oracle;

    /// @notice Mapping of addresses that are allowed to mint nodes
    mapping(address => bool) public adminMinters;

    /// @dev The interface ID of the ERC2981 NFT Royalty Standard.
    bytes4 private constant _INTERFACE_ID_ERC2981 = 0x2a55205a;

    /// @dev The interface ID of the ERC721 NFT Standard.
    bytes4 private constant _INTERFACE_ID_ERC721 = 0x80ac58cd;

    /// @dev The number of categories that can exist.
    uint256 private constant NUM_CATEGORIES = 4;

    /// @dev Tracks the NFT that exists (or not) at a given index.
    mapping(uint => Building) private nfts;

    /// @dev Stores the given mint data for a category and it's child buildings and tiers.
    mapping(uint => mapping(uint => BuildingDetails[])) private categories;

    /// @dev The total number of buildings that belong in a category.
    uint256[] public maxCategoryBuildings = new uint256[](NUM_CATEGORIES);

    /// @dev Modifier for allowing set addresses to mint for other addresses.
    modifier onlyMinter() {
        if (adminMinters[msg.sender] != true) {
            revert UnauthorizedMinter();
        }
        _;
    }

    constructor(
        string memory _name,
        string memory _symbol,
        address _oracleAddress
    ) ERC721u(_name, _symbol) {
        _registerInterface(_INTERFACE_ID_ERC721);
        _registerInterface(_INTERFACE_ID_ERC2981);

        oracle = IPriceOracle(_oracleAddress);
    }

    /**
     * @notice Returns whether the given interface is supported by
     *         this contract.
     *
     * @param interfaceId   The ID of the interface to check.
     *
     * @return Whether the given `interfaceId` is supported.
     */
    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        virtual
        override(ERC721, IERC165, ERC165Storage)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    /**
     * @notice Retrieves the NFT at the given index.
     *
     * @param tokenId   The ID of the token to retrieve.
     *
     * @return The NFT token data.
     */
    function getNFT(uint256 tokenId) external view returns (Building memory) {
        _assertTokenExists(tokenId);

        return nfts[tokenId];
    }

    /**
     * @notice Returns the current number of tokens that exist.
     *
     * @return The total number of tokens in existence.
     */
    function getTotalSupply() external view returns (uint256) {
        return totalSupply;
    }

    /**
     * @notice Retrieves the current price of 3ULL from the Price
     *         Oracle.
     *
     * @param _price    The USD price to get in 3ULL.
     *
     * @return The input price in 3ULL.
     */
    function getBullPrice(uint256 _price) public view returns (uint256) {
        return oracle.getPriceIn("USDC", "3ULL", _price);
    }

    /**
     * @notice Retrieves the royalty information for this ERC2981 contract.
     *
     * @param _salePrice    The price of the sale to get royalty information
     *                      for.
     *
     * @return receiver         The receiving address for the royalties.
     * @return royaltyAmount    The amount of royalties.
     */
    function royaltyInfo(
        uint256,
        uint256 _salePrice
    ) external view override returns (address receiver, uint256 royaltyAmount) {
        return (royaltyAddress, (_salePrice * 500) / 10000);
    }

    /**
     * @notice Retrieves the building details for a given building and tier.
     *         A buildings details includes data such as the type of building,
     *         the purchase price, the current minted amount of that building,
     *         and the total number of that building that can be minted.
     *
     * @param categoryId The ID of the category the building belongs to.
     * @param buildingId The initial ID of the building in the details
     *                   mapping.
     * @param tierId     The building tier ID.
     *
     * @return The building details for the given building and tier IDs.
     */
    function getBuildingDetails(
        uint256 categoryId,
        uint256 buildingId,
        uint256 tierId
    ) external view returns (BuildingDetails memory) {
        _assertCategoryInRange(categoryId);

        return categories[categoryId][buildingId][tierId];
    }

    /**
     * @notice Mints a building with the given tier to the sender. This
     *         function is payable and therefore requires payment in 3ULL in
     *         order to mint.
     *
     * @param categoryId The category the building belongs to.
     * @param buildingId The building that is to be minted.
     * @param tierId     The tier of the building to mint.
     */
    function mint(
        uint256 categoryId,
        uint256 buildingId,
        uint256 tierId
    ) external payable {
        _assertValidValueProvided(categoryId, buildingId, tierId);

        _mintBuilding(msg.sender, categoryId, buildingId, tierId);
    }

    /**
     * @notice Allows an address toggled as an admin minter to mint a building
     *         for an address.
     *
     * @param to            The address that will receive the minted building.
     * @param categoryId    The category the building belongs to.
     * @param buildingId    The building index to mint.
     * @param tierId        The building tier that will be minted.
     */
    function mintFor(
        address to,
        uint256 categoryId,
        uint256 buildingId,
        uint256 tierId
    ) external onlyMinter {
        _mintBuilding(to, categoryId, buildingId, tierId);
    }

    /**
     * @notice Allows the owner to set the maximum buildings for a given
     *         category.
     *
     * @param categoryId    The category ID to set the max buildings.
     * @param max           The maximum buildings that can exist on the
     *                      category.
     */
    function setMaxBuildings(
        uint256 categoryId,
        uint256 max
    ) external onlyOwner {
        _assertCategoryInRange(categoryId);

        maxCategoryBuildings[categoryId] = max;
    }

    /**
     * @notice Allows the owner to set the details of a building.
     *
     * @param categoryId        The category index that the building belons
     *                          to.
     * @param buildingId        The index that will be used to lookup and
     *                          mutate this building at.
     * @param _name             The name of the building.
     * @param _maxCountPerTier  The maximum of this building that can be
     *                          minted at each tier.
     * @param _tierPrices       The building prices at each tier, must be
     *                          the same length as the _maxCountPerTier
     *                          parameter.
     */
    function setBuildingDetails(
        uint256 categoryId,
        uint256 buildingId,
        string memory _name,
        uint256[] memory _maxCountPerTier,
        uint256[] memory _tierPrices
    ) external onlyOwner {
        _assertCategoryInRange(categoryId);

        if (_maxCountPerTier.length != _tierPrices.length) {
            revert ArraysDoNotMatch();
        }

        uint256[] memory _minted = new uint256[](_tierPrices.length);
        for (uint i = 0; i < _minted.length; i++) {
            if (categories[categoryId][buildingId].length <= i) {
                _minted[i] = 0;
                continue;
            }

            if (categories[categoryId][buildingId][i].max > 0) {
                // If the maximum value has been set, then we can assume
                // that we are updating the building details, so retrieve
                // the current minted amount.
                _minted[i] = categories[categoryId][buildingId][i].minted;

                delete categories[categoryId][buildingId][i];
            }
        }

        for (uint i = 0; i < _minted.length; i++) {
            if (categories[categoryId][buildingId].length <= i) {
                categories[categoryId][buildingId].push(
                    BuildingDetails({
                        name: _name,
                        price: _tierPrices[i],
                        minted: _minted[i],
                        max: _maxCountPerTier[i]
                    })
                );
            } else {
                categories[categoryId][buildingId][i] = BuildingDetails({
                    name: _name,
                    price: _tierPrices[i],
                    minted: _minted[i],
                    max: _maxCountPerTier[i]
                });
            }

            emit BuildingTierUpdated(
                categoryId,
                buildingId,
                i,
                _name,
                _maxCountPerTier[i],
                _tierPrices[i]
            );
        }
    }

    /**
     * @notice Allows the owner to update the price of a building tier.
     *
     * @param buildingId    The ID of the building to update.
     * @param tierId        The building tier ID to update.
     * @param newPrice      The new price of the building tier.
     */
    function updateTierPrice(
        uint256 categoryId,
        uint256 buildingId,
        uint256 tierId,
        uint256 newPrice
    ) external onlyOwner {
        if (categories[categoryId][buildingId].length >= tierId) {
            categories[categoryId][buildingId][tierId].price = newPrice;

            emit BuildingTierUpdated(
                categoryId,
                buildingId,
                tierId,
                categories[categoryId][buildingId][tierId].name,
                categories[categoryId][buildingId][tierId].max,
                newPrice
            );
        }
    }

    /**
     * @notice Sets the address where royalties will be sent.
     *
     * @param _royaltyAddress   The address that will receive royalties.
     */
    function setRoyaltyAddress(address _royaltyAddress) external onlyOwner {
        royaltyAddress = _royaltyAddress;
    }

    /**
     * @notice Allows the owner to withdraw deposited funds.
     *
     * @param amount    The amount of funds to withdraw.
     */
    function withdraw(uint256 amount) external onlyOwner {
        payable(msg.sender).transfer(amount);
    }

    /**
     * @notice Registers that this contract does support a given interface ID.
     *
     * @param _interfaceId  The ID of the interface to register.
     */
    function registerInterface(bytes4 _interfaceId) external onlyOwner {
        _registerInterface(_interfaceId);
    }

    /**
     * @notice Toggles whether the given address can mint without requiring
     *         payment or not.
     *
     * @param minter The address of the minter to toggle.
     */
    function toggleAdminMinter(address minter) external onlyOwner {
        adminMinters[minter] = !adminMinters[minter];

        emit AdminMinterToggled(minter, adminMinters[minter]);
    }

    /**
     * @dev Internal function to mint a building at a certain tier, to a
     *      given address.
     *
     * @param to             The receiving address of the newly minted
     *                       token/building.
     * @param _categoryId    The ID of the category the building belongs to
     * @param _buildingId    The ID of the building, or type of building to
     *                       mint.
     * @param _tierId        The tier of the building to mint.
     */
    function _mintBuilding(
        address to,
        uint256 _categoryId,
        uint256 _buildingId,
        uint256 _tierId
    ) internal {
        _assertCategoryInRange(_categoryId);

        if (_buildingId >= maxCategoryBuildings[_categoryId]) {
            revert UnknownBuilding(_categoryId, _buildingId);
        }

        _assertTierNotMintedOut(_categoryId, _buildingId, _tierId);

        categories[_categoryId][_buildingId][_tierId].minted++;

        nfts[totalSupply] = Building({
            categoryId: _categoryId,
            buildingId: _buildingId,
            tierId: _tierId
        });

        _safeMint(to, totalSupply);

        emit TokenMinted(to, totalSupply);

        totalSupply++;
    }

    /**
     * @dev Internal view function to revert if the given building
     *      and tier has reached the maximum number of mints.
     *
     * @param categoryId    The ID of the category.
     * @param buildingId    The ID of the building.
     * @param tierId        The ID of the building tier to verify
     *                      that it has or has not minted out.
     */
    function _assertTierNotMintedOut(
        uint256 categoryId,
        uint256 buildingId,
        uint256 tierId
    ) internal view {
        if (
            categories[categoryId][buildingId][tierId].minted ==
            categories[categoryId][buildingId][tierId].max
        ) {
            revert BuildingTierMintedOut(
                categoryId,
                buildingId,
                tierId,
                categories[categoryId][buildingId][tierId].max
            );
        }
    }

    /**
     * @dev Internal view function to revert if the given value in a
     *      payable transaction is invalid.
     *
     * @param categoryId    The ID of the category.
     * @param buildingId    The ID of the building.
     * @param tierId        The ID of the building tier to verify the
     *                      price.
     */
    function _assertValidValueProvided(
        uint256 categoryId,
        uint256 buildingId,
        uint256 tierId
    ) internal view {
        uint256 basePrice = categories[categoryId][buildingId][tierId].price;
        uint256 convertedPrice = getBullPrice(basePrice);

        if (msg.value != convertedPrice) {
            revert InsufficientValueProvided(msg.value, convertedPrice);
        }
    }

    /**
     * @dev Internal view function to revert if the given category ID
     *      is not within the bounds of the categories.
     *
     * @param given The given category ID.
     */
    function _assertCategoryInRange(uint256 given) internal pure {
        if (given < 0 || given >= NUM_CATEGORIES) {
            revert InvalidCategoryIndex();
        }
    }
}

contracts/oracle/price/interfaces/IPrice.content:
pragma solidity 0.8.17;

/**
 * @title IPriceOracle
 * @author Jourdan (PLAYA3ULL)
 * @notice IPriceOracle contains all external functions, structs
 *         and events and errors for the Price Oracle.
 */
interface IPriceOracle {
    /**
     * @dev Emit an event whenever a token is set or updated.
     *
     * @param symbol The token symbol.
     */
    event TokenUpdated(string indexed symbol);

    /**
     * @dev Emit an event whenever a token pair's price is set
     *      or updated.
     *
     * @param symbolA The symbol of the `from` token.
     * @param symbolB The symbol of the `to`, or `in` token.
     * @param price   The new price of the tokenA in tokenB.
     */
    event PairPriceUpdated(
        string indexed symbolA,
        string indexed symbolB,
        uint256 price
    );

    /**
     * @dev Revert with an error when trying to set a token pair
     *      price that is zero or less.
     */
    error InvalidPrice();

    function getPriceIn(
        string memory tokenFrom,
        string memory tokenIn,
        uint256 price
    ) external view returns (uint256);

    function getPairPrice(
        string memory symbolA,
        string memory symbolB
    ) external view returns (uint256, uint256);
}

contracts/nft/ERC721u.sol.content:
pragma solidity 0.8.17;

import "@openzeppelin/contracts/access/Ownable.sol";

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

/**
 * @title ERC721u
 * @author Jourdan (PLAYA3ULL)
 * @notice This contract is intended to be inherited and provides
 *         access to a central location for handling retrieving,
 *         updating, and verifying ERC721 token URIs which the base
 *         ERC721 standard does not provide.
 *         Additionally, this ERC721u contract supports emitting
 *         events when the token URI is updated, allowing listeners
 *         to handle this change.
 */
contract ERC721u is ERC721, Ownable {
    /// @notice The base URI for tokens and where their metadata
    ///         is stored.
    string public baseTokenURI;

    /**
     * @dev Emit an event whenever the base token URI is updated.
     *
     * @param uri   The new base token uri.
     */
    event BaseTokenURIUpdated(string uri);

    /**
     * @dev Revert with an error when trying to access a token ID
     *      that does not exist.
     */
    error TokenDoesNotExist();

    constructor(
        string memory _name,
        string memory _symbol
    ) ERC721(_name, _symbol) {}

    /**
     * @dev Internal view function to view and return the base token URI.
     *
     * @return The base token URI where the metadata is stored.
     */
    function _baseURI() internal view virtual override returns (string memory) {
        return baseTokenURI;
    }

    /**
     * @notice Returns the token URI where the metadata is stored for
     *         a given token ID.
     *
     * @param tokenId   The ID of the token to retrieve the URI for.
     *
     * @return The token metadata URI.
     */
    function tokenURI(
        uint256 tokenId
    ) public view virtual override returns (string memory) {
        _assertTokenExists(tokenId);

        return
            string(abi.encodePacked(baseTokenURI, Strings.toString(tokenId)));
    }

    /**
     * @notice Sets the base token metadata URI.
     *
     * @param _newBaseTokenURI  The new base token URI.
     */
    function setBaseTokenURI(
        string memory _newBaseTokenURI
    ) external onlyOwner {
        baseTokenURI = _newBaseTokenURI;

        emit BaseTokenURIUpdated(baseTokenURI);
    }

    /**
     * @dev Internal view function to revert if the given token ID does
     *      not exist.
     *
     * @param tokenId   The ID of the token to verify if exists.
     */
    function _assertTokenExists(uint256 tokenId) internal view {
        if (!_exists(tokenId)) {
            revert TokenDoesNotExist();
        }
    }
}

contracts/nft/games/mars-colony/interfac.content:
pragma solidity 0.8.17;

/**
 * @title IColonyMarsNFT
 * @author Jourdan (PLAYA3ULL)
 * @notice IColonyMarsNFT contains all external functions, structs,
 *         events, and errors for the Colony Mars NFT.
 */
interface IColonyMarsNFT {
    /**
     * @dev A building contains three components: a numeric
     *      index for the building, a numeric index for the
     *      tier of the building, and the type of building.
     */
    struct Building {
        uint256 categoryId;
        uint256 buildingId;
        uint256 tierId;
    }

    /**
     * @dev The details for a building outline the required
     *      data to be stored on-chain such as the price of
     *      the building, the number of them minted and the
     *      max of the tier that can be minted.
     */
    struct BuildingDetails {
        string name;
        uint256 price;
        uint256 minted;
        uint256 max;
    }

    /**
     * @dev Emit an event whenever a buildings tier is updated.
     *
     * @param categoryId    The index of the category.
     * @param buildingId    The index of the building.
     * @param tierId        The index of the building tier.
     * @param name          The name of the building.
     * @param maxCount      The number of tokens that can be minted at this tier.
     * @param price         The price of the token at this tier.
     */
    event BuildingTierUpdated(
        uint256 indexed categoryId,
        uint256 indexed buildingId,
        uint256 indexed tierId,
        string name,
        uint256 maxCount,
        uint256 price
    );

    /**
     * @dev Emit an event whenever a token is minted.
     *
     * @param to        The address that the token was minted
     *                  to.
     * @param tokenId   The ID of the token that was minted.
     */
    event TokenMinted(address indexed to, uint256 indexed tokenId);

    /**
     * @dev Emit an event whenever the royalty address is updated.
     *
     * @param royalty   The royalty address that has been set.
     */
    event RoyaltyAddressUpdated(address royalty);

    /**
     * @notice                 Emitted when an address' minter access is toggled
     * @param minterAddress    The address of the minter
     * @param canMint          Whether the toggled address can mint
     */
    event AdminMinterToggled(address minterAddress, bool canMint);

    /**
     * @dev Revert with an error when trying to mint a building tier
     *      that has minted out.
     *
     * @param categoryId The ID of the category.
     * @param buildingId The ID of the building.
     * @param tierId     The ID of the tier that the building belongs.
     * @param max        The maximum number of this tier that can be
     *                   minted.
     */
    error BuildingTierMintedOut(
        uint256 categoryId,
        uint256 buildingId,
        uint256 tierId,
        uint256 max
    );

    /**
     * @dev Revert with an error when a given value that was provided
     *      was not equal to an expected value.
     */
    error InsufficientValueProvided(uint256 provided, uint256 expected);

    /**
     * @dev Revert with an error when supplying two arrays that were
     *      meant to match, but didn't.
     */
    error ArraysDoNotMatch();

    /**
     * @dev Revert with an error when an address attempts to mint for
     *      without having access to that function.
     */
    error UnauthorizedMinter();

    /**
     * @dev Revert with an error when supplying a category index
     *      outside of the expected category IDs.
     */
    error InvalidCategoryIndex();

    /**
     * @dev Revert with an error when attempting to purchase a building
     *      that does not exist.
     */
    error UnknownBuilding(uint256 categoryId, uint256 buildingId);
}

