contracts/nft/vi3/VI3.sol.content:
pragma solidity 0.8.17;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";

import {ERC721u} from "../ERC721u.sol";
import {IPLAYA3ULLVI3EventsAndErrors} from "./interfaces/IVI3EventsAndErrors.sol";

/**
 * @title PLAYA3ULL Games VI3 Contract
 * @author Jourdan (PLAYAY3ULL), Sam (PLAYA3ULL)
 * @notice This contract is the central VI3 NFT contract
 *         for PLAYA3ULL Games' Very Important 3ULLs.
 */
contract PLAYA3ULLVI3 is ERC721u, IPLAYA3ULLVI3EventsAndErrors {
    /// @notice The price of each VI3
    uint256 public price;

    /// @notice The total minted number of tokens
    uint256 public totalSupply;

    /// @notice Whether VI3s are tradeable or not
    bool public tradeable;

    constructor() ERC721u("VERY IMPORTANT 3ULL", "VI3") {
        price = 10000 * 1e18;
        tradeable = false;
    }

    /**
     * @notice Mints the provided wallet a VI3 NFT,
     *
     * @param to    The receiver of the minted NFT.
     */
    function mint(address to) external payable {
        _assertEqualDeposit(msg.value);
        _safeMint(to, totalSupply);

        emit VI3Minted(to, totalSupply);

        unchecked {
            totalSupply++;
        }
    }

    /**
     * @notice Sets the price to purchase a NFT.
     *
     * @param _price    The new purchase price.
     */
    function setPrice(uint256 _price) external onlyOwner {
        price = _price;

        emit PriceUpdated(_price);
    }

    /**
     * @notice Toggles whether NFTs are tradeable.
     */
    function toggleTradeable() external onlyOwner {
        tradeable = !tradeable;

        emit TradeableUpdated(tradeable);
    }

    /**
     * @notice Allows the owner to withdraw deposited funds.
     *
     * @param amount    The amount of funds to withdraw.
     */
    function withdraw(uint256 amount) external onlyOwner {
        _assertEnoughFunds(amount);
        payable(msg.sender).transfer(amount);
    }

    /**
     * @dev Internal function to override the `_beforeTokenTransfer`
     *      hook provided by the ERC721 standard.
     *      Prevents transferring the NFT if they are not tradeable.
     *
     * @param from      The address that is attempting to transfer.
     * @param to        The address that will receive the token.
     * @param tokenId   The ID of the token to transfer.
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal override(ERC721) {
        super._beforeTokenTransfer(from, to, tokenId);

        if (from != address(0)) {
            _assertTokenTradeable();
        }

        _assertReceiverHasNoTokens(to);
    }

    /**
     * @dev Internal function to revert if the deposit amount is not equal
     *      to the purchase price of an NFT.
     *
     * @param amount    The amount that is being deposited.
     */
    function _assertEqualDeposit(uint256 amount) internal view {
        if (amount != price) {
            revert DepositNotEqual(price);
        }
    }

    /**
     * @dev Internal function to revert if the owner attempts to withdraw
     *      more funds than the contract balance contains.
     *
     * @param requestedAmount   The amount that is being withdrawn from
     *                          the contract.
     */
    function _assertEnoughFunds(uint256 requestedAmount) internal view {
        if (requestedAmount > address(this).balance) {
            revert NotEnoughFunds(address(this).balance);
        }
    }

    /**
     * @dev Internal view function to revert if a token is being
     *      transferred but tokens are not currently set to allow transfers.
     */
    function _assertTokenTradeable() internal view {
        if (!tradeable) {
            revert NonTransferrableToken();
        }
    }

    /**
     * @dev Internal view function to revert when transferring to a wallet
     *      that already owns a token.
     *
     * @param receiver  The wallet to verify the number of tokens held.
     */
    function _assertReceiverHasNoTokens(address receiver) internal view {
        if (balanceOf(receiver) >= 1) {
            revert ReceiverAlreadyOwnsToken();
        }
    }
}

contracts/nft/vi3/interfaces/IVI3EventsA.content:
pragma solidity 0.8.17;

/**
 * @title IPLAYA3ULLVI3EventsAndErrors
 * @author Jourdan (PLAYA3ULL)
 * @notice IPLAYA3ULLVI3EventsAndErrors contains all external
 *         events and errors for the Very Important 3ULLs.
 */
interface IPLAYA3ULLVI3EventsAndErrors {
    /**
     * @dev Emit an event whenever a new VI3 NFT is minted.
     *
     * @param to        The address of the receiver of the new NFT.
     * @param tokenId   The ID of the new NFT.
     */
    event VI3Minted(address to, uint256 tokenId);

    /**
     * @dev Emit an event whenever the price of a VI3 is updated.
     *
     * @param price The new price of a VI3 NFT.
     */
    event PriceUpdated(uint256 price);

    /**
     * @dev Emit an event whenever the VI3 NFTs become tradeable.
     *
     * @param tradeable Whether the NFTs are tradeable or not.
     */
    event TradeableUpdated(bool tradeable);

    /**
     * @dev Revert with an error when attempting to purchase a
     *      token without supplying the correct deposit amount.
     *
     * @param price The expected price of the token.
     */
    error DepositNotEqual(uint256 price);

    /**
     * @dev Revert with an error when trying to transfer a token
     *      that is not tradeable.
     */
    error NonTransferrableToken();

    /**
     * @dev Revert with an error when trying to transfer a token
     *      to a wallet that already owns a token.
     */
    error ReceiverAlreadyOwnsToken();

    /**
     * @dev Revert with an error when trying to withdraw balance
     *      that is greater than the balance of the contract.
     */
    error NotEnoughFunds(uint256 balance);
}

contracts/nft/bull/BULLNFTStorage.sol.content:
pragma solidity 0.8.17;

import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";

import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165Storage.sol";
import "@openzeppelin/contracts-upgradeable/interfaces/IERC2981Upgradeable.sol";

import "./lib/MathUtils.sol";

import {ERC721uUpgradeable} from "../ERC721uUpgradeable.sol";
import {IPLAYA3ULLNFT} from "./interfaces/I3ULLNFT.sol";
import {IPLAYA3ULLNFTEventsAndErrors} from "./interfaces/I3ULLNFTEventsAndErrors.sol";

/**
 * @title PLAYA3ULL Games 3ULL NFT Storage Contract
 * @author Jourdan (PLAYA3ULL)
 * @notice This contract contains the storage and administration
 *         functionality for the 3ULL NFT tokens.
 */
contract PLAYA3ULLNFTStorage is
    Initializable,
    ERC721uUpgradeable,
    IERC2981Upgradeable,
    ERC165Storage,
    UUPSUpgradeable,
    IPLAYA3ULLNFT,
    IPLAYA3ULLNFTEventsAndErrors
{
    /// @notice Represents the maximum number of tokens that can
    ///         exist.
    uint256 public maxSupply;

    /// @notice The total number of tokens that currently exist.
    uint256 public totalSupply;

    /// @notice The address of the minting contract that is
    ///         authorized to mint on this contract.
    address public minterAddress;

    /// @notice The address where royalties will be sent.
    address public royaltyAddress;

    /// @notice Tracks whether the given DNA exists or not.
    mapping(string => bool) public dnaExists;

    /// @dev Running counter of the number of traits that exist.
    uint256 private numTraits;

    /// @dev The interface ID of the ERC2981 NFT Royalty Standard.
    bytes4 private constant _INTERFACE_ID_ERC2981 = 0x2a55205a;

    /// @dev The interface ID of the ERC721 NFT Standard.
    bytes4 private constant _INTERFACE_ID_ERC721 = 0x80ac58cd;

    /// @dev Tracks the NFT that exists (or not) at a given index.
    mapping(uint256 => NFT) private nfts;

    /// @dev Tracks the trait information at a given index.
    mapping(uint256 => Trait) private traits;

    /// @dev Tracks the attributes that exist at a given index.
    mapping(uint256 => Attribute[]) private attributes;

    /// @dev Tracks the addresses that are approved to transfer
    ///      tokens without needing all to be sold.
    mapping(address => bool) private approvedTransferrers;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        string memory _name,
        string memory _symbol,
        uint256 _maxSupply
    ) public initializer {
        __ERC721u_init(_name, _symbol);
        __UUPSUpgradeable_init();

        _registerInterface(_INTERFACE_ID_ERC721);
        _registerInterface(_INTERFACE_ID_ERC2981);

        maxSupply = _maxSupply;
    }

    /**
     * @notice Returns whether the given interface is supported by
     *         this contract.
     *
     * @param interfaceId   The ID of the interface to check.
     *
     * @return Whether the given `interfaceId` is supported.
     */
    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        virtual
        override(ERC721Upgradeable, IERC165Upgradeable, ERC165Storage)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    /**
     * @notice Retrieves the NFT at the given index.
     *
     * @param tokenId   The ID of the token to retrieve.
     *
     * @return The NFT token data.
     */
    function getNFT(uint256 tokenId) external view returns (NFT memory) {
        _assertTokenExists(tokenId);

        return nfts[tokenId];
    }

    /**
     * @notice Returns the total number of tokens that may exist.
     *
     * @return The maximum supply of tokens.
     */
    function getMaxSupply() external view override returns (uint256) {
        return maxSupply;
    }

    /**
     * @notice Returns the current number of tokens that exist.
     *
     * @return The total number of tokens in existence.
     */
    function getTotalSupply() external view override returns (uint256) {
        return totalSupply;
    }

    /**
     * @notice Returns the number of traits that have been added.
     *
     * @return The number of traits.
     */
    function getTraitLength() external view override returns (uint256) {
        return numTraits;
    }

    /**
     * @notice Retrieves the trait information at the given index.
     *
     * @param traitId   The index of the trait to get.
     *
     * @return The trait data.
     */
    function getTrait(
        uint256 traitId
    ) external view override returns (Trait memory) {
        _assertValidTraitRange(traitId);

        return traits[traitId];
    }

    /**
     * @notice Returns the number of attributes at a given trait index.
     *
     * @param traitId   The index of the trait where the attributes are
     *                  stored.
     *
     * @return The number of attributes.
     */
    function getAttributeLength(
        uint256 traitId
    ) external view override returns (uint256) {
        return attributes[traitId].length;
    }

    /**
     * @notice Retrieves the attribute information at the given trait index
     *         and attribute index.
     *
     * @param traitId   The index of the trait that this attribute belongs to.
     * @param attrId    The index of the attribute that is being retrieved.
     *
     * @return The attribute data.
     */
    function getAttribute(
        uint256 traitId,
        uint256 attrId
    ) external view override returns (Attribute memory) {
        _assertValidTraitRange(traitId);
        _assertValidAttributeRange(traitId, attrId);

        return attributes[traitId][attrId];
    }

    /**
     * @notice Determines whether the given trait array results in a DNA that
     *         already exists.
     *
     * @param _traits   The trait array to compute on.
     *
     * @return isUnique Whether the computed DNA is unique.
     * @return dna      The computed DNA.
     */
    function isDNAUnique(
        uint256[] memory _traits
    ) public view override returns (bool isUnique, string memory dna) {
        // The concatenated dna string before hashing
        string memory dnaBase = "";

        for (uint8 i = 0; i < numTraits; ) {
            // Build the DNA string
            bytes memory trait = bytes(Strings.toString(_traits[i]));

            // Zero-pad in order to allow similar but different trait arrays to have unique DNA
            // Example: [10, 10, 10, 1, 0, 1, 0, 1, 0, 1]
            //          [1, 0, 1, 0, 1, 0, 10, 10, 10, 1]
            // Are two different arrays, but look as follows when in their raw string forms:
            // 1. 1010101010101
            // 2. 1010101010101
            // Which results in both having the same DNA, even though they are separate tokens.
            // Performing a zero-pad allows the strings to look as follows:
            // 1. 10101001000100010001
            // 2. 01000100010010101001
            if (_traits[i] < 10) {
                dnaBase = string.concat(dnaBase, "0");
            }

            dnaBase = string.concat(dnaBase, string(trait));

            unchecked {
                i++;
            }
        }

        // Hash the DNA string using keccak256
        dna = MathUtils._bytes32ToString(MathUtils._hash(string(dnaBase)));

        isUnique = !dnaExists[dna];
    }

    /**
     * @notice Retrieves the royalty information for this ERC2981 contract.
     *
     * @param _salePrice    The price of the sale to get royalty information
     *                      for.
     *
     * @return receiver         The receiving address for the royalties.
     * @return royaltyAmount    The amount of royalties.
     */
    function royaltyInfo(
        uint256,
        uint256 _salePrice
    ) external view override returns (address receiver, uint256 royaltyAmount) {
        return (royaltyAddress, (_salePrice * 750) / 10000);
    }

    /**
     * @notice Mints a 3ULL NFT.
     *
     * @param to        The receiving address of the minted token.
     * @param traitIds  The trait IDs for the token to mint.
     */
    function mint(address to, uint256[] memory traitIds) external override {
        _assertSenderIsAuthorized(msg.sender);
        _assertValidTraitCount(traitIds.length);
        _assertTokensNotSoldOut();

        (bool _isUnique, string memory _dna) = isDNAUnique(traitIds);
        if (!_isUnique) {
            revert DuplicateTokenDNA();
        }

        for (uint8 i = 0; i < numTraits; ) {
            unchecked {
                // Increment the number of this trait that has been sold
                attributes[i][traitIds[i]].numSold++;
                i++;
            }
        }

        nfts[totalSupply] = NFT({dna: _dna, traits: traitIds});

        dnaExists[_dna] = true;

        _safeMint(to, totalSupply);

        emit TokenMinted(to, totalSupply);

        unchecked {
            totalSupply++;
        }
    }

    /**
     * @notice Creates a new trait.
     *
     * @param _name     The name of the trait
     * @param _required Whether the trait is required.
     * @param _maxCount The maximum number of attributes the trait can hold.
     */
    function addTrait(
        string memory _name,
        bool _required,
        uint256 _maxCount
    ) external onlyOwner {
        traits[numTraits] = Trait({
            name: _name,
            required: _required,
            maxCount: _maxCount
        });

        emit TraitAdded(numTraits, _name, traits[numTraits]);

        unchecked {
            numTraits++;
        }
    }

    /**
     * @notice Creates a new attribute for a trait.
     *
     * @param _traitId  The index of the trait that this attribute will
     *                  belong to.
     * @param _name     The name of the attribute.
     * @param _rarity   The rarity value of the attribute.
     */
    function addAttribute(
        uint256 _traitId,
        string memory _name,
        uint256 _rarity
    ) public onlyOwner {
        _assertValidTraitRange(_traitId);

        attributes[_traitId].push(
            Attribute({name: _name, rarity: _rarity, numSold: 0})
        );

        emit AttributeAdded(
            _traitId,
            attributes[_traitId].length - 1,
            _name,
            attributes[_traitId][attributes[_traitId].length - 1]
        );
    }

    /**
     * @notice Creates multiple attributes at once.
     *
     * @param _traitId      The index of the trait that these attributes
     *                      will belong to.
     * @param _attributes   The attributes data.
     */
    function addMultipleAttributes(
        uint256 _traitId,
        Attribute[] memory _attributes
    ) external onlyOwner {
        _assertValidTraitRange(_traitId);

        if (attributes[_traitId].length > 0) {
            revert ExistingAttributeLengthMustBeZero();
        }

        for (uint8 i = 0; i < _attributes.length; i++) {
            addAttribute(_traitId, _attributes[i].name, _attributes[i].rarity);
        }
    }

    /**
     * @notice Sets the address of the minter.
     *         Verifies whether the provided address is a contract, and if not
     *         reverts.
     *
     * @param minter    The address of the minting contract.
     */
    function setMinterAddress(address minter) external onlyOwner {
        uint32 size;
        assembly {
            // Verify whether the provided address is a contract
            size := extcodesize(minter)
        }

        // The size of the code at the address is zero, therefore is not a contract
        if (size == 0) {
            revert AddressIsNotContract();
        }

        minterAddress = minter;
    }

    /**
     * @notice Sets the address where royalties will be sent.
     *
     * @param _royaltyAddress   The address that will receive royalties.
     */
    function setRoyaltyAddress(address _royaltyAddress) external onlyOwner {
        royaltyAddress = _royaltyAddress;
    }

    /**
     * @notice Sets the maximum number of tokens that can be minted.
     *
     * @param max   The new maximum number of tokens.
     */
    function setMaxSupply(uint256 max) external onlyOwner {
        maxSupply = max;
    }

    /**
     * @notice Toggles whether the given `transferrer` is approved to make
     *         token transfers without needing all tokens to be sold.
     *
     * @param transferrer   The address of the transferrer to toggle approval.
     */
    function toggleApprovedTransferrer(address transferrer) external onlyOwner {
        approvedTransferrers[transferrer] = !approvedTransferrers[transferrer];
    }

    /**
     * @notice Registers that this contract does support a given interface ID.
     *
     * @param _interfaceId  The ID of the interface to register.
     */
    function registerInterface(bytes4 _interfaceId) external onlyOwner {
        _registerInterface(_interfaceId);
    }

    /**
     * @dev Internal function to override the `_beforeTokenTransfer`
     *      hook provided by the ERC721 standard.
     *      Prevents transferring the NFT if they are not tradeable.
     *
     * @param from      The address that is attempting to transfer.
     * @param to        The address that will receive the token.
     * @param tokenId   The ID of the token to transfer.
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId,
        uint256
    ) internal virtual override(ERC721Upgradeable) {
        super._beforeTokenTransfer(from, to, tokenId, 1);

        if (from == address(0)) return;

        // Only possibly revert when there are tokens still to be sold, and
        // the transferrer, either `from` or `to` are not approved.
        if (tokenId < maxSupply && totalSupply < maxSupply) {
            if (!approvedTransferrers[from] && !approvedTransferrers[to]) {
                revert TransferOnlyWhenSold(totalSupply, maxSupply);
            }
        }
    }

    /**
     * @dev Internal view function to revert if the tokens have all sold.
     */
    function _assertTokensNotSoldOut() internal view {
        if (totalSupply >= maxSupply) {
            revert TokensSoldOut();
        }
    }

    /**
     * @dev Internal view function to revert if the provided trait count
     *      is not equal to the expected trait count.
     *
     * @param count The provided trait count.
     */
    function _assertValidTraitCount(uint256 count) internal view {
        if (count != numTraits) {
            revert InvalidTraitCountProvided(numTraits);
        }
    }

    /**
     * @dev Internal view function to revert if the provided trait value
     *      is outside the range of traits.
     *
     * @param provided  The provided trait value.
     */
    function _assertValidTraitRange(uint256 provided) internal view {
        if (provided >= numTraits && provided >= 0) {
            revert InvalidTraitRange(numTraits);
        }
    }

    /**
     * @dev Internal view function to revert if the provided attribute
     *      value is within range for the given trait value.
     *
     * @param _traitId  The ID of the trait where the attribute resides.
     * @param provided  The provided attribute index.
     */
    function _assertValidAttributeRange(
        uint256 _traitId,
        uint256 provided
    ) internal view {
        if (provided >= traits[_traitId].maxCount && provided >= 0) {
            revert InvalidAttributeRange(traits[_traitId].maxCount);
        }
    }

    /**
     * @dev Internal view function to revert if the sender of a call
     *      is not authorized to access a function that requires
     *      authorization.
     *
     * @param sender    The address to check authority on.
     */
    function _assertSenderIsAuthorized(address sender) internal view {
        if (sender != owner() && sender != minterAddress) {
            revert UnauthorizedAccessAttempt();
        }
    }

    /**
     * @dev Internal function to allow upgrades to this contract.
     *
     * @param newImplementation The address of the new implementation.
     */
    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}
}

contracts/nft/bull/lib/MathUtils.sol.content:
pragma solidity 0.8.17;

/**
 * @title PLAYA3ULL Games Math Utility Library
 * @author Jourdan (PLAYA3ULL)
 * @notice This library contains utility functions for calculating
 *         various mathematical values that Solidity does not readily
 *         provide.
 */
library MathUtils {
    int256 internal constant SCALE = 1e18;
    
    ///
    /// @dev Hashes given string using keccak256
    ///
    function _hash(string memory _string) internal pure returns (bytes32) {
        return keccak256(abi.encodePacked(_string));
    }
    
    ///
    /// @dev Converts bytes16 to bytes32
    ///
    function _bytes16ToBytes32(bytes16 data) internal pure returns (bytes32 result) {
        result = bytes32 (data) & 0xFFFFFFFFFFFFFFFF000000000000000000000000000000000000000000000000 |
            (bytes32 (data) & 0x0000000000000000FFFFFFFFFFFFFFFF00000000000000000000000000000000) >> 64;
        result = result & 0xFFFFFFFF000000000000000000000000FFFFFFFF000000000000000000000000 |
            (result & 0x00000000FFFFFFFF000000000000000000000000FFFFFFFF0000000000000000) >> 32;
        result = result & 0xFFFF000000000000FFFF000000000000FFFF000000000000FFFF000000000000 |
            (result & 0x0000FFFF000000000000FFFF000000000000FFFF000000000000FFFF00000000) >> 16;
