        return x >= 0 ? uint256(x) : uint256(-x);
    }

    function max(uint256 a, uint256 b) internal pure returns (uint256) {
        return a >= b ? a : b;
    }

    function min(uint256 a, uint256 b) internal pure returns (uint256) {
        return a <= b ? a : b;
    }

    function canSub(uint256 a, uint256 b) internal pure returns (bool) {
        if (b > a) return (false);
        return (true);
    }
}

contracts/nft/ERC721uUpgradeable.sol.content:
pragma solidity 0.8.17;

import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import "@openzeppelin/contracts-upgradeable/utils/StringsUpgradeable.sol";

/**
 * @title ERC721uUpgradeable
 * @author Jourdan (PLAYA3ULL)
 * @notice This contract is intended to be inherited and provides
 *         access to a central location for handling retrieving,
 *         updating, and verifying ERC721 token URIs which the base
 *         ERC721 standard does not provide.
 *         Additionally, this ERC721u contract supports emitting
 *         events when the token URI is updated, allowing listeners
 *         to handle this change.
 */
contract ERC721uUpgradeable is ERC721Upgradeable, OwnableUpgradeable {
    /// @notice The base URI for tokens and where their metadata
    ///         is stored.
    string public baseTokenURI;

    /**
     * @dev Emit an event whenever the base token URI is updated.
     *
     * @param uri   The new base token uri.
     */
    event BaseTokenURIUpdated(string uri);

    /**
     * @dev Revert with an error when trying to access a token ID
     *      that does not exist.
     */
    error TokenDoesNotExist();

    function __ERC721u_init(
        string memory _name,
        string memory _symbol
    ) internal onlyInitializing {
        __ERC721_init(_name, _symbol);
        __Ownable_init();
    }

    /**
     * @dev Internal view function to view and return the base token URI.
     *
     * @return The base token URI where the metadata is stored.
     */
    function _baseURI() internal view virtual override returns (string memory) {
        return baseTokenURI;
    }

    /**
     * @notice Returns the token URI where the metadata is stored for
     *         a given token ID.
     *
     * @param tokenId   The ID of the token to retrieve the URI for.
     *
     * @return The token metadata URI.
     */
    function tokenURI(
        uint256 tokenId
    ) public view virtual override returns (string memory) {
        _assertTokenExists(tokenId);

        return
            string(
                abi.encodePacked(
                    baseTokenURI,
                    StringsUpgradeable.toString(tokenId)
                )
            );
    }

    /**
     * @notice Sets the base token metadata URI.
     *
     * @param _newBaseTokenURI  The new base token URI.
     */
    function setBaseTokenURI(
        string memory _newBaseTokenURI
    ) external onlyOwner {
        baseTokenURI = _newBaseTokenURI;

        emit BaseTokenURIUpdated(baseTokenURI);
    }

    /**
     * @dev Internal view function to revert if the given token ID does
     *      not exist.
     *
     * @param tokenId   The ID of the token to verify if exists.
     */
    function _assertTokenExists(uint256 tokenId) internal view {
        if (!_exists(tokenId)) {
            revert TokenDoesNotExist();
        }
    }
}

contracts/nft/bull/interfaces/I3ULLNFT.s.content:
pragma solidity 0.8.17;

/**
 * @title IPLAYA3ULLNFT
 * @author Jourdan (PLAYA3ULL)
 * @notice IPLAYA3ULLNFT contains all external functions and
 *         structs for the 3ULL NFT.
 */
interface IPLAYA3ULLNFT {

    /**
     * @dev An NFT contains two components: a string that
     *      represents that NFTs DNA, preventing any other
     *      token from using the same DNA, and an array of
     *      traits that the NFT is constructed from.
     */
    struct NFT {
        string dna;
        uint256[] traits;
    }

    /**
     * @dev A trait represents the different parts of the
     *      token. Each trait contains three components: 
     *      a name, whether it is required, and the maximum
     *      number of attributes that this trait may hold.
     */
    struct Trait {
        string name;
        bool required;
        uint256 maxCount;
    }

    /**
     * @dev An attribute belongs to a trait, and represents
     *      a different and unique variation of the parent
     *      trait. Each attribute contains three components:
     *      a name, a rarity value, and the number of this
     *      attribute that has sold.
     */
    struct Attribute {
        string name;
        uint256 rarity;
        uint256 numSold;
    }

    function getMaxSupply() external view returns (uint256);

    function getTotalSupply() external view returns (uint256);

    function getTraitLength() external view returns (uint256);
    
    function getTrait(uint256 traitId) external view returns (Trait memory);

    function getAttributeLength(uint256 traitId) external view returns (uint256);

    function getAttribute(uint256 traitId, uint256 attrId) external view returns (Attribute memory);

    function isDNAUnique(uint256[] memory traitIds) external view returns (bool isUnique, string memory dna);

    function mint(address to, uint256[] memory traitIds) external;
}

contracts/nft/bull/interfaces/I3ULLNFTEv.content:
pragma solidity 0.8.17;

import {IPLAYA3ULLNFT} from "./I3ULLNFT.sol";

/**
 * @title IPLAYA3ULLNFTEventsAndErrors
 * @author Jourdan (PLAYA3ULL)
 * @notice IPLAYA3ULLNFTEventsAndErrors contains all external events,
 *         and errors for the 3ULL NFT.
 */
interface IPLAYA3ULLNFTEventsAndErrors is IPLAYA3ULLNFT {
    /**
     * @dev Emit an event whenever a token is minted.
     *
     * @param to        The address that the token was minted
     *                  to.
     * @param tokenId   The ID of the token that was minted.
     */
    event TokenMinted(address indexed to, uint256 indexed tokenId);

    /**
     * @dev Emit an event whenever a new trait is added.
     *
     * @param traitIndex    The index that the new trait resides at.
     * @param name          The name of the added trait.
     * @param addedTrait    The trait that was added.
     */
    event TraitAdded(uint256 indexed traitIndex, string name, Trait addedTrait);

    /**
     * @dev Emit an event whenever a new attribute is added.
     *
     * @param traitIndex    The index of the trait that this attribute
     *                      belongs to.
     * @param attrIndex     The index that the new attribute resides at.
     * @param name          The name of the added attribute.
     * @param addedAttr     The attribute that was added.
     */
    event AttributeAdded(
        uint256 indexed traitIndex,
        uint256 indexed attrIndex,
        string name,
        Attribute addedAttr
    );

    /**
     * @dev Emit an event whenever the royalty address is updated.
     *
     * @param royalty   The royalty address that has been set.
     */
    event RoyaltyAddressUpdated(address royalty);

    /**
     * @dev Revert with an error when trying to mint when all the
     *      tokens are sold out.
     */
    error TokensSoldOut();

    /**
     * @dev Revert with an error when providing an invalid number
     *      of traits.
     *
     * @param expected  The expected number of traits.
     */
    error InvalidTraitCountProvided(uint256 expected);

    /**
     * @dev Revert with an error when trying to mint a token that
     *      has the same DNA as another that already exists.
     */
    error DuplicateTokenDNA();

    /**
     * @dev Revert with an error when providing a value that is out
     *      of range for a trait.
     * @param maxBound  The maximum upper bound trait available.
     */
    error InvalidTraitRange(uint256 maxBound);

    /**
     * @dev Revert with an error when providing a value that is out
     *      of range for an attribute.
     * @param maxBound  The maximum upper bound attribute available.
     */
    error InvalidAttributeRange(uint256 maxBound);

    /**
     * @dev Revert with an error when a user that is not authorized
     *      attempts to perform an action that only authorized users
     *      may attempt.
     */
    error UnauthorizedAccessAttempt();

    /**
     * @dev Revert with an error when setting a contract address that
     *      is not actually a contract.
     */
    error AddressIsNotContract();

    /**
     * @dev Revert with an error when attempting to transfer a token
     *      when there are still tokens to sell.
     *
     * @param numSold   The number of tokens that have been sold.
     * @param maxSupply The number of tokens that must be sold in
     *                  order to transfer a token.
     */
    error TransferOnlyWhenSold(uint256 numSold, uint256 maxSupply);

    /**
     * @dev Revert with an error when trying to add multiple attributes
     *      but attributes already exist.
     */
    error ExistingAttributeLengthMustBeZero();
}

